#include<bits/stdc++.h>using namespace std;long long t,c[10005];string s;int main() {    cin>>t;    while(t--)    {        memset(c,false,sizeof(c));        cin>>s;        long long n=s.size();        s=" "+s;        s=s+" ";        int cnt=1;        for(int i=2;i<=n+1;i++)        {            if(s[i]==s[i-1])            {                cnt++;            }            else            {                if(cnt%2==1)                {                    c[s[i-1]-'a'+1]=true;                                    }                cnt=1;            }        }        for(int i=1;i<=26;i++)        {            if(c[i])            {                cout<<char('a'-1+i);            }        }        cout<<'\n';    }}