#include <bits/stdc++.h>using namespace std;string s;long long n;int main (){    cin>>n;    while(n--)    {        cin>>s;        long long x=s.size()-1;        if(s[0]=='t' and s[1]=='m' and s[2]=='a' and s[3]=='t' and s[4]=='h')        {            cout<<"YES"<<'\n';        }        else if(s[0]=='t' and s[1]=='m' and s[2]=='a' and s[3]=='t' and s[x]=='h')        {            cout<<"YES"<<'\n';        }        else if(s[0]=='t' and s[1]=='m' and s[2]=='a' and s[x-1]=='t' and s[x]=='h')        {            cout<<"YES"<<'\n';        }        else if(s[0]=='t' and s[1]=='m' and s[x-2]=='a' and s[x-1]=='t' and s[x]=='h')        {            cout<<"YES"<<'\n';        }else if(s[0]=='t' and s[x-3]=='m' and s[x-2]=='a' and s[x-1]=='t' and s[x]=='h')        {            cout<<"YES"<<'\n';        }        else if(s[x-4]=='t' and s[x-3]=='m' and s[x-2]=='a' and s[x-1]=='t' and s[x]=='h')        {            cout<<"YES"<<'\n';        }        else        {            cout<<"NO"<<'\n';        }    }}