#include <bits/stdc++.h>using namespace std;#define ll long longstring a,b,c;int x,y,k,s,q,i,nl,am=false,dem=0;int main(){    cin>>a>>b;    x=a.size();    y=b.size();        if(x>y)    {        s=x-y;        for(i=0;i<s;i++) b='0'+b;    }    else if(x<y)    {        q=y-x;        for(i=0;i<q;i++) a='0'+a;    }    if(a<b)    {        swap(a,b);        am=true;    }    k=a.size();    c="";    for(i=k-1;i>=0;i--)    {         long long kq=a[i]-b[i]-nl;        nl=0;        if(kq<0)        {            nl=1;            kq+=10;        }        c=char(kq+'0')+c;    }    while(c[0]=='0')    {        c.erase(0,1);    }    if(c=="")    {        cout<<"0";        return 0;    }    if(am==true)    {         cout<<'-';     }    cout<<c;}